import React from 'react';
import ReactDOM from "react-dom";
import './App.css';



/*
    PSEUDO CODE

    array of usernames

    for each username in the array
        data = require(username)
        call SocialCard(passing in the data)
*/

const myUsernames = ['username-accenture_us.json', 'username-cristiano.json',
                     'username-rihanna.json', 'username-twitterdev.json'];

function MyApp() {
    let arr = [];

    for (let i=0; i < myUsernames.length; ++i) {
        let path = myUsernames[i];
        let data = require("./" + path);
        arr.push(data);
    }
    return (
        <SocialCard props={arr} />
    );
}

function SocialCard({ props }) {
    return props.map(prop => (
        <div className="card">
            <img src={prop.profile_banner_url} className="banner" />
            <img src={prop.profile_image_url} className="pro-pic" />
            {prop.verified &&
                <img className="verified" src={'https://upload.wikimedia.org/wikipedia/commons/e/e4/Twitter_Verified_Badge.svg'} />
            }
            <button className="follow-button">Follow</button>
            <h1 className="name">{prop.name}</h1>
            <h2 className="screen-name">@{prop.screen_name}</h2>
            <p className="description">{prop.description}</p>
            <p className="info-description">
                <img className="icon" src={require('./images/location-icon.svg')} />{prop.location} &nbsp;&nbsp;
                <img className="icon" src={require('./images/url-icon.png')} />{prop.url} &nbsp;&nbsp;
                <img className="icon" src={require('./images/bday-icon.png')} />Born date &nbsp;&nbsp;
                <img className="icon" src={require('./images/calendar-icon.png')} />Joined /*{joined_date_string}*/ &nbsp;&nbsp;
            </p>
            <p className="info-description">{prop.friends_count} Following {prop.followers_count} Followers</p>
        </div>
    ));
}

ReactDOM.render(
    <MyApp />,
    document.getElementById('root')
);
