import React from 'react';
import ReactDOM from "react-dom";
import './App.css';



/*
    PSEUDO CODE

    array of usernames

    for each username in the array
        data = require(username)
        call SocialCard(passing in the data)
*/

const myUsernames = ['username-accenture_us.json', 'username-cristiano.json',
                     'username-rihanna.json', 'username-twitterdev.json'];

function MyApp() {
    let arr, date_arr = [];
    for (let i=0; i < myUsernames.length; ++i) {
        let path = myUsernames[i];
        let data = require("./" + path);
        arr.push(data);
        joined_date = Date.parse(data.created_at);
        joined_date_object = new Date(joined_date);
        joined_year = joined_date_object.getFullYear();
        joined_month = joined_date_object.toLocaleString('default', {month: 'long'});
        joined_date_string = joined_month + ' ' + joined_year;
    }
    return (
        <SocialCard props={arr} />
    );
}

function SocialCard({ data }) {
    return data.map(datum => (
        <h1>{datum.name}</h1>
    ));
}

ReactDOM.render(
    <MyApp />,
    document.getElementById('root')
);
