import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import './App.css';
import axios from 'axios';

function SocialCard() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        axios.get(`https://api.randomuser.me/`)
            .then(res => {
                console.log(res)
                setPosts(res.data)
            })
            .catch(err => {
                console.log(err)
            })
    }, [])

    return (
        <div>
            <ul>
                {posts.map(post => (
                    <li key='0'>{post.gender}</li>
                ))}
            </ul>
        </div>
