import React from "react";
import ReactDOM from "react-dom";
import './App.css';

function SocialCard() {
    return (
        <div className="card">
            <img src={require("./images/twitter-banner.png")} className="banner" />
            <img src={require("./images/twitter-logo-profile.png")} className="pro-pic" />
            <button className="follow-button">Follow</button>
            {/*include verification in name*/}
            <h1 className="name">name here</h1>
            <h2 className="screen-name">@handle here</h2>
            <p>description here</p>
            <p>location URL Born date</p>
            <p>1,807 Following 507.9 K Followers</p>
        </div>
    );
}

ReactDOM.render(<SocialCard />, document.getElementById("root"));
