import React from "react";
import ReactDOM from "react-dom";

function SocialCard() {
    return (
        <div>
            <img src={"../images/twitter-banner.png"} className="banner" />
            <img src={"../images/twitter-logo-profile.png"} className="pro-pic" />
            // put "follow" button here
            // include verification in name
            <h1 className="name">name here</h1>
            <br />
            <h2 className="screen-name">@handle here</h2>
            <br />
            <p>description here</p>
            // location, link, bday with icons
            // join date with icon
            // number following, number of followers
            <p>
                <p style={{fontWeight: "bold"}}>1,807</p>
                <p>Following</p>
                <p style={{fontWeight: "bold"}}>507.9 K</p>
                <p>Followers</p>
            </p>
        </div>
    );
}

ReactDOM.render(<SocialCard />, document.getElementById("root"));
